mapper.select:
		select t1.*, (select t2.name from sys_dept t2 where t2.id=t1.dept_id) deptName,
        (SELECT t3.NAME FROM sys_tenant t3 WHERE t3.id = t1.tenant_id) tenantName
			from sys_user t1 where t1.super_admin = 0

			and t1.username like #{username}


			and t1.dept_id = #{deptId}


            and t1.tenant_id = #{tenantId}


			and t1.gender = #{gender}


			and t1.id != #{selfId}


			and t1.dept_id in

				#{id}



            and t1.tenant_id in

                #{id}
